//전체화면 회색 배경

import React from 'react';
import styled from 'styled-components';
import palette from '../../lib/styles/palette';
import Button from '../common/Button';

const BackTemplate= styled.div`

position:absolute;
left:0;
right:0;
top:0;
bottom:0;

background:${palette.gray[2]};

// display: flex;
// flex-direction: column;
// justify-content: center;
// align-items: center;


`;

const MainTitle=styled.div`

height:20%;
font-size:2rem;
font-weight:bold;
color:${palette.cyan[7]};

//텍스트 수평 수직 중앙 정렬 
display:flex;
align-items:center;
justify-content:center;
flex-direction:column;

border-bottom:1px solid black;
`;

const WhiteBox = styled.div`
  .logo-area {
    display: block;
    padding-bottom: 2rem;
    text-align: center;
    font-weight: bold;
    letter-spacing: 2px;
  }
  box-shadow: 0 0 8px rgba(0, 0, 0, 0.025);
  padding: 2rem;
  width: 360px;
  background: black;
  border-radius: 2px;
`;


const ContentBody=styled.div`
  
height:70%;
border-top:2rem;
display:flex;
flex-direction:row;
flex-wrap:nowrap; // 한줄에 표사
justify-content:flex-start;


`;

const StyleButtonTemplate=styled.div`

width:50%;

display:flex;
flex-direction:column;
justify-content:center;
align-items:center;


border-bottom:1px solid black;
border-right:1px solid black;
border-left: 2rem;
border-left:1px solid black;
`;


const StyledButton=styled(Button)`

  margin:3rem;
  width:150px;

`;

const ReadMe=styled.div`

width:100%;
border-bottom:1px solid black;


`;

//수강신청 스케줄러
//시간표 짜기
//결과 보기
const MainTemplate=({children})=>{


    return (
        
        <BackTemplate>    
          <MainTitle>설문조사</MainTitle>
      

          <ContentBody>


            <StyleButtonTemplate>
        
            <StyledButton>설문 조사 시작</StyledButton>
            <StyledButton>결과 보기</StyledButton>

            </StyleButtonTemplate>
            
            <ReadMe>설문조사 작성 홈페이지입니다.</ReadMe>



          </ContentBody>

      </BackTemplate>


    );
}



export default MainTemplate;
