const GraphContainer=()=>{


    const [TdrawList,setTdrawList]=useState([{
        id:1,
        name:"회로이론"
    
    },{
    
        id:2,
        name:"알고리즘"
    },{
        id:3,
        name:"논리회로"
    
    }]);


    const [makingdrawList,setmakingdrawList]=useState([]);


    const Delete=(id)=>{

       const  aa=TdrawList.filter(e=>(e.id!==id));
       
       setTdrawList(aa);
          
      };
      
      
      useEffect(()=>{},TdrawList);
    

    // const dispatch = useDispatch();

    const {type1,type2,type3}=useSelector(({write})=>({

       type1:write.type1,
       type2:write.type2,
       type3:write.type3,
    }
    )); 

    const Maintable={
        type1,type2,type3
    };
    




    // 배열로 시간표 리스트만 넘기면됨 
    const makingDrawList=useCallback((arr)=>{

        let makingTable=[];

        for(let i=0;i<arr.length;i++){  // 한 과목씩 살펴봄

            for(let j=0;j<arr[i].classtime.keyword.length;j++){  // 한 과목의 요일수
                
                let day;

                switch(arr[i].classtime.keyword[j]){
                    case 't1': day=5; break;
                    case 't2': day=24; break;
                    case 't3': day=43; break;
                    case 't4': day=62; break;
                    case 't5': day=81; break;
                    default: break;

                }

              

              let dayarr=  arr[i].classtime[arr[i].classtime.keyword[j]]; // 요일 배열
                
              let timeleng=dayarr.length * 3.5;
              let time=9+((dayarr[0]-2)*3.5);

                makingTable.push({x:day,y:time,ycolor:timeleng,color:"#CEF6F5",text:arr[i].classname});
            
            }


        }
        
        setmakingdrawList(makingTable);


    },[TdrawList]);

    
    //개수기반
    //우선순위 1개수가 같으면 일단 그거 다출력

    const Calculate=()=>{

        let finalrank=[]; //최종 개수 기반 순위


        const root1=composeTree('type1',Maintable);

        let maxOneTable=[];    //개수 기반

        
        for(let i=0;i<root1.finalTable.length;i++){

            
            let n=root1.finalTable[i];

            if(n.deep===root1.max){

                maxOneTable.push(n);
            }

        }


        //1 개수 같은것들끼리 모아둠  -> 또 경쟁

        for(let i=0;i<maxOneTable.length;i++){
            
            let table

            for(let j=0;j<root1.max;j++){
                let n= maxOneTable[i];


            }
            


        }
        
        


        

        





    }



    const onCalculate=()=>{
        
        const root= composeTree('type1',Maintable,[]);
        
         
         let maxScoreTable=[];
         let maxTable=[];
     
         for(let i=0;i<root.finalTable.length;i++){
     
             let n=root.finalTable[i];
     
            if(n.score===root.maxScore){
     
             maxScoreTable.push(n);
     
     
            }    
     
     
            if(n.deep===root.max){
     
             maxTable.push(n);
            }
     
         }
         console.log('&&&&&&&&&&&&&&&학점 우선 시간표&&&&&&&&&&&&&&');
         for(let i=0;i<maxScoreTable.length;i++){
     
             let n=maxScoreTable[i];
             let k=n.deep;
             console.log('--------%d번째 시간표----------',i+1);
             for(let j=0;j<k;j++){
     
                 console.log('%d:%s %s %d ',j+1,n.data.classname,n.data.profess,n.data.classscore);
     
                 n=n.adault;
             }
     
             console.log('[총 %d학점]',root.maxScore);
             console.log('---------------------------');
     
         }
     
     
         console.log('&&&&&&&&&&&&&&&과목개수 우선 시간표&&&&&&&&&&&&&&');
     
        for(let i=0;i<maxTable.length;i++)
        {
            let n=maxTable[i];
            let a=n.score;
            console.log('--------%d번째 시간표----------',i+1);
             for(let j=0;j<root.max;j++)
             {   
                
                 console.log('%d:%s %s %d',j+1 ,n.data.classname,n.data.profess,n.data.classscore);
                 n=n.adault;
     
             }
             console.log('[총%d학점]',a);
             console.log('---------------------------');
        }
        
        let a=maxTable[0];
        let bb=[];
        for(let i=0;i<root.max;i++){

            bb.push(a.data);
            a=a.adault;


        }
        makingDrawList(bb);


     };

     


    return (
        <Graph onCalculate={onCalculate}
            TdrawList={TdrawList}
            Delete={Delete}
            makingdrawList={makingdrawList}
            >

            </Graph>
    );


};


export default GraphContainer;
