import React , {useState,useCallback,useEffect} from 'react';
import {useSelector,useDispatch} from 'react-redux';
import {withRouter} from 'react-router-dom';
import WriteSchedule from '../../component/write/WriteSchedule';

import {register} from '../../modules/write';

import Node ,{lookschedule,insertTable,composeTree ,search} from '../../lib/tree/tree';



const WriteContainer=({history})=>{

     const dispatch = useDispatch();

     const {type1,type2,type3}=useSelector(({write})=>({

        type1:write.type1,
        type2:write.type2,
        type3:write.type3,
     }
     ));  // write 가 관리하고 있는 상태를 불러와서 write에 저장


// 1번 순회함.


//type1이 변경되면 onCalculate 함수를 실행 




// useEffect(()=>{history.push('/graph')},[onCalculate]);

const onCalculate=(table)=>{
    
   
   const root= composeTree('type1',table);
   
    //먼저 type1 로 root 만들고
    //학점이 남으면 type2로 root만듬
    //그래도 학점이 남으면 type3으로 root 만듬.
    
    let maxScoreTable=[];
    let maxTable=[];

    for(let i=0;i<root.finalTable.length;i++){

        let n=root.finalTable[i];

       if(n.score===root.maxScore){

        maxScoreTable.push(n);


       }    


       if(n.deep===root.max){

        maxTable.push(n);
       }

    }
    console.log('&&&&&&&&&&&&&&&학점 우선 시간표&&&&&&&&&&&&&&');
    for(let i=0;i<maxScoreTable.length;i++){

        let n=maxScoreTable[i];
        let k=n.deep;
        console.log('--------%d번째 시간표----------',i+1);
        for(let j=0;j<k;j++){

            console.log('%d:%s %s %d ',j+1,n.data.classname,n.data.profess,n.data.classscore);

            n=n.adault;
        }

        console.log('[총 %d학점]',root.maxScore);
        console.log('---------------------------');

    }


    console.log('&&&&&&&&&&&&&&&과목개수 우선 시간표&&&&&&&&&&&&&&');

   for(let i=0;i<maxTable.length;i++)
   {
       let n=maxTable[i];
       let a=n.score;
       console.log('--------%d번째 시간표----------',i+1);
        for(let j=0;j<root.max;j++)
        {   
           
            console.log('%d:%s %s %d',j+1 ,n.data.classname,n.data.profess,n.data.classscore);
            n=n.adault;

        }
        console.log('[총%d학점]',a);
        console.log('---------------------------');
   }

  

history.push('/graph');



};

const onFinal=({type,classname,classtime,profess,classscore})=>{

        

        dispatch(
            register({
                form:type,
                value:{
                  classname,
                  classtime,
                  profess,
                  classscore,
                 }
                
            })
        );
        
      
    
    };




     return (

        <WriteSchedule 
        
        onFinal={onFinal}
    onCalculate={onCalculate}
       />

     );

};



export default withRouter(WriteContainer);
