    t1:[],
    t2:[],
    t3:[],
    t4:[],
    t5:[]


});


class Node {

    constructor(data){

        this.data=data;
        this.children=[];
        this.adault=null;
    }


   insert(data){

    const node=new Node(data);
    node.adault=this;

    this.children.push(node);

   }
   


}



//시간표 변환
const classTimeMod=function(c){

let schedule={

    t1:[],
    t2:[],
    t3:[],
    t4:[],
    t5:[]

};

let n=-1;
let t=null;

for(let i=0;i<c.length;i++){
   
    if(n===3)       
        {
            n=2;
            continue;
        }
    
    if(n===2)
        {
            n=-1;
            continue;
        }

    if(c[i]===' ')
        continue;

    switch(c[i]){

        case '월':
            t='t1';
            break;
        case '화':
            t='t2';
            break;
        case '수':
            t='t3';
            break;
        case '목':
            t='t4';
            break;
        case '금':
            t='t5';
            break;    
        
        default:
            break;
    
    }

    if(c[i]>='A' && c[i]<='J')
        {   let b=c[i]-'A';
            b=(b+1)*3;

            schedule[t].push(b-1);
            schedule[t].push(b);
            schedule[t].push(b+1);

            continue;
        }

    else if(c[i]>='1' && c[i]<='9')
        {   
            
            if(c[i+1]>='0' && c[i+1]<='9')
                {
                    n=1;
                    continue;
                }
            else if(n===1)
                {
                    let b=20+(c[i]-'0');
                    n=-1;

                    if(c[i+1]==='.')
                        {
                            n=3;
                            schedule[t].push(b+1);
                        }
                    else
                        schedule[t].push(b);
                
                }

            else {

                if(c[i+1]==='.')
                    {  n=3;
                        schedule[t].push((c[i]-'0')+1);
                        }
                else
                    schedule[t].push(c[i]-'0');
            }

        }

    
    


}//for문 끝


return schedule;

};


