const WriteContainer=({history})=>{

     const dispatch = useDispatch();

     const write=useSelector(state=>(

        state.write
        
     ),[]);  // write 가 관리하고 있는 상태를 불러와서 write에 저장

     

const onFinal=({imp,classname,classtime,profess,classscore})=>{

        let type;

        if(imp==='1')
            type='type1';
        else if(imp==='2')
            type='type2';
        else if(imp==='3')
            type='type3';

        
        dispatch(
            register({
                form:type,
                value:{
                  classname,
                  classtime,
                  profess,
                  classscore,
                 }
                
            })
        );
        
    
    };




     return (

        <WriteSchedule 
        
        onFinal={onFinal}

        />

     );

};



