import React , {useState} from 'react';
import {useSelector,useDispatch} from 'react-redux';
import {withRouter} from 'react-router-dom';
import WriteSchedule from '../../component/write/WriteSchedule';

import {register} from '../../modules/write';



class Node {

    constructor(data){

        this.data=data;
        this.children=[];
        this.adault=null;
    }


   insert(data){

    const node=new Node(data);
    node.adault=this;

    this.children.push(node);

   }
   


}




//둘의 시간이 겹치는지 확인
const lookschedule= function(a,b){

        if(a.t1.length)
            {   
                if(b.t1.length)
                {
                    for(let i=0;i<a.t1.length;i++){
                        
                        if(b.t1.indexOf(a.t1[i])!==-1)
                            return false;
                            
                    }

                }

            }
    
        if(a.t2.length)
        {   
            if(b.t2.length)
            {
                for(let i=0;i<a.t2.length;i++){
                    
                    if(b.t2.indexOf(a.t2[i])!==-1)
                        return false;
                        
                }

            }

        }

        if(a.t3.length)
        {   
            if(b.t3.length)
            {
                for(let i=0;i<a.t3.length;i++){
                    
                    if(b.t3.indexOf(a.t3[i])!==-1)
                        return false;
                        
                }

            }

        }

        if(a.t4.length)
        {   
            if(b.t4.length)
            {
                for(let i=0;i<a.t4.length;i++){
                    
                    if(b.t4.indexOf(a.t4[i])!==-1)
                        return false;
                        
                }

            }

        }

        if(a.t5.length)
        {   
            if(b.t5.length)
            {
                for(let i=0;i<a.t5.length;i++){
                    
                    if(b.t5.indexOf(a.t5[i])!==-1)
                        return false;
                        
                }

            }

        }


        return true;
    


};



const WriteContainer=({history})=>{

     const dispatch = useDispatch();

     const write=useSelector(state=>(

        state.write
        
     ),[]);  // write 가 관리하고 있는 상태를 불러와서 write에 저장

     

const onFinal=({imp,classname,classtime,profess,classscore})=>{

        let type;

        if(imp==='1')
            type='type1';
        else if(imp==='2')
            type='type2';
        else if(imp==='3')
            type='type3';

        
        dispatch(
            register({
                form:type,
                value:{
                  classname,
                  classtime,
                  profess,
                  classscore,
                 }
                
            })
        );
        
    
    };




     return (

        <WriteSchedule 
        
        onFinal={onFinal}

        />

     );

};



export default WriteContainer;




