//전체화면 회색 배경

import React from 'react';
import styled from 'styled-components';
import palette from '../../lib/styles/palette';


const BackTemplate= styled.div`

position:absolute;
left:0;
right:0;
top:0;
bottom:0;

background:${palette.gray[2]};

// display: flex;
// flex-direction: column;
// justify-content: center;
// align-items: center;


`;

const MainTitle=styled.div`

height:20%;
font-size:2rem;
font-weight:bold;
color:${palette.cyan[7]};

//텍스트 수평 수직 중앙 정렬 
display:flex;
align-items:center;
justify-content:center;
flex-direction:column;

border-bottom:1px solid black;
`;

const WhiteBox = styled.div`
  .logo-area {
    display: block;
    padding-bottom: 2rem;
    text-align: center;
    font-weight: bold;
    letter-spacing: 2px;
  }
  box-shadow: 0 0 8px rgba(0, 0, 0, 0.025);
  padding: 2rem;
  width: 360px;
  background: black;
  border-radius: 2px;
`;


const ContentBody=styled.div`
  
height:70%;
border-top:2rem;
display:flex;
flex-direction:row;
flex-wrap:nowrap; // 한줄에 표사
justify-content:flex-start;


`;

const StyleButton=styled.div`

width:50%;

display:flex;
flex-direction:column;
justify-content:center;
align-items:center;


border-bottom:1px solid black;





`;

const MainTemplate=({children})=>{


    return (
        
        <BackTemplate>    
          <MainTitle>수강신청 스케줄러</MainTitle>
      

          <ContentBody>

            <StyleButton> 

            <button>시간표 짜기</button>
            <button>결과 보기 </button>
            </StyleButton>




          </ContentBody>

            {/* <WhiteBox>
        <div className="logo-area">
          react
        </div>
        {children}
      </WhiteBox>
       */}
      </BackTemplate>


    );
}



export default MainTemplate;
